/**
 * 微信分享分装接口，可以直接压缩后放到all.js里面
 *
 * @author zak
 * @date 2015.3.10
 */
(function() {
    window.wxinfo = window.wxinfo || {};
    var WxApi = function() {
        // wx分享到朋友圈成功
        this.shareTimeline = {
            doneList: [],
            failList: []
        };

        // 分享给朋友
        this.shareAppMessage = {
            doneList: [],
            failList: []
        };
    };

    WxApi.fn = WxApi.prototype;

    /**
     * 给分享到朋友圈增加回调函数
     *
     * @param {Function} doneFn 成功函数
     * @param {Function} failFn 失败函数
     */
    WxApi.fn.addShareTimeLineCb = function addShareTimeLineSucc(doneFn, failFn) {
        if(typeof doneFn === 'function') this.shareTimeline.doneList.push(doneFn);
        if(typeof failFn === 'function') this.shareTimeline.failList.push(failFn);
    };
    /**
     * 给分享给好友增加回调函数
     *
     * @param {Function} doneFn 成功函数
     * @param {Function} failFn 失败函数
     */
    WxApi.fn.addShareAppMessageCb = function addShareTimeLineSucc(doneFn, failFn) {
        if(typeof doneFn === 'function') this.shareTimeline.doneList.push(doneFn);
        if(typeof failFn === 'function') this.shareTimeline.failList.push(failFn);
    };

    // 暴露接口
    window.wxApi = new WxApi();
})();


(function() {
    // 获取微信配置
    $.ajax({
        url: '/iwx/config/get_jsconfig',
        data: {
            url: window.location.href
        },
        success: function(data){
            var d = JSON.parse(data).data;

            //d.debug = true;
            d.jsApiList = ['onMenuShareTimeline',
                'onMenuShareAppMessage',
                'getNetworkType',
                'startRecord',
                'stopRecord',
                'uploadVoice',
                'playVoice',
                'downloadVoice',
                'onVoicePlayEnd',
                'onVoiceRecordEnd'];
            wx.config(d);
        },
        error: function(xhr, type){
        }
    });

    // 微信已准备好
    wx.ready(function() {
        var wxApi = window.wxApi;
        var wxObj = window.liveApp.caseData.wx;
        var caseData = window.liveApp.caseData;

        // 去掉默认的数据
        if(/wx-yl-logo/.test(wxObj.share.icon)) {
            wxObj.share.icon = caseData.logo;
        } else if(!wxObj.share.icon) {
            wxObj.share.icon = caseData.logo;
        }

        if(/wx-yl-logo/.test(wxObj.toFriend.icon)) {
            wxObj.toFriend.icon = caseData.logo;
        } else if(!wxObj.toFriend.icon) {
            wxObj.toFriend.icon = caseData.logo;
        }

        // 分享到朋友圈
        wx.onMenuShareTimeline({
            title: wxObj.share.title || document.title || 'liveapp', // 分享标题
            link: '', // 分享链接
            imgUrl: wxObj.share.icon || 'http://' + window.location.host + '/res/images/logo.jpg', // 分享图标
            success: function (res) {
                _.each(wxApi.shareTimeline.doneList, function(it) {
                    if(it) it();
                });

                // 跳转连接
                if(wxObj.link) window.location.href = wxObj.link;
            },
            cancel: function () {
                _.each(wxApi.shareTimeline.failList, function(it) {
                    if(it) it();
                })
            }
        });

        //分享给好友
        wx.onMenuShareAppMessage({
            title: wxObj.toFriend.title || document.title || 'liveapp', // 分享标题
            desc: wxObj.toFriend.content || ((wxObj.toFriend.title || document.title) + window.location.href),
            link: '', // 分享链接
            imgUrl: wxObj.toFriend.icon || 'http://' + window.location.host + '/res/images/logo.jpg', // 分享图标
            type: '', // 分享类型,music、video或link，不填默认为link
            dataUrl: '', // 如果type是music或video，则要提供数据链接，默认为空
            success: function (res) {
                _.each(wxApi.shareAppMessage.doneList, function(it) {
                    if(it) it();
                });

                // 跳转连接
                if(wxObj.link) window.location.href = wxObj.link;
            },
            cancel: function () {
                _.each(wxApi.shareAppMessage.failList, function(it) {
                    if(it) it();
                })
            }
        });

        // 获取当前微信分享接口
        wx.getNetworkType({
            success: function (res) {
                window.wxinfo = window.wxinfo || {};
                window.wxinfo.networkType = res.networkType; // 返回网络类型2g，3g，4g，wifi
            }
        });
    });

    wx.error(function(res){
        alert("微信配置失败")
        console.log('配置微信失败！');
    });

})();

// 隐藏 apploading 图标
(function () {
    $(document).ready(function () {
        $('#app-loading').hide();
    });
})();
